using Microsoft.EntityFrameworkCore;\n\nnamespace MacBookFinder.API.Data\n{\n    public class MacBookContext : DbContext\n    {\n        public MacBookContext(DbContextOptions<MacBookContext> options) : base(options) { }\n\n        public DbSet<MacBook> MacBooks { get; set; }\n\n        protected override void OnModelCreating(ModelBuilder modelBuilder)\n        {\n            modelBuilder.Entity<MacBook>().HasData(\n                new MacBook { Id = 1, Model = "Air M1", Price = 999 },\n                new MacBook { Id = 2, Model = "Air M2", Price = 1199 },\n                new MacBook { Id = 3, Model = "Pro 14 M3", Price = 1999 },\n                new MacBook { Id = 4, Model = "Pro 16 M3 Max", Price = 3999 }\n            );\n        }\n    }\n\n    public class MacBook\n    {\n        public int Id { get; set; }\n        public string Model { get; set; }\n        public decimal Price { get; set; }\n    }\n}