using System.Collections.Generic;\nusing System.Linq;\nusing Microsoft.EntityFrameworkCore;\nusing MacBookFinder.API.Data;\nusing MacBookFinder.API.Models;\nusing MacBookFinder.API.Services.Interfaces;\n\nnamespace MacBookFinder.API.Services\n{\n    public class MacBookService : IMacBookService\n    {\n        private readonly MacBookContext _context;\n\n        public MacBookService(MacBookContext context)\n        {\n            _context = context;\n        }\n\n        public MacBook FindCheapestMacBook()\n        {\n            return _context.MacBooks.OrderBy(m => m.Price).FirstOrDefault();\n        }\n\n        public IEnumerable<MacBook> FilterByPriceRange(decimal minPrice, decimal maxPrice)\n        {\n            return _context.MacBooks.Where(m => m.Price >= minPrice && m.Price <= maxPrice).ToList();\n        }\n    }\n}